import React from 'react'
import { FaLaptop, FaTableAlt, FaMobileAlt } from 'react-icons/fa'

const Header = ({ title, width }) => {
  return (
    <header className='Header'>
      <h2>{title}</h2>
      {width < 768 ? <FaMobileAlt />
        : width < 992 ? <FaTableAlt />
          : <FaLaptop />}

    </header>
  )
}

export default Header
