import { createContext, useState } from "react";
import { useNavigate } from "react-router-dom";

const DataContext = createContext({});

export const DataProvider = ({ children }) => {
    const [search, setSearch] = useState("");
    const [posts, setPosts] = useState([]);

    const navigate = useNavigate(); // Ensure navigation is handled properly

    const handleAddPost = (title, body) => {
        const newPost = {
            id: posts.length + 1, // Generate a unique ID (can be improved with UUID)
            title,
            body,
            datetime: new Date().toLocaleString(), // Proper date format
        };
        setPosts([...posts, newPost]); // Update state with the new post
        navigate("/"); // Redirect to home after posting
    };

    const handleDelete = (id) => {
        setPosts(posts.filter(post => post.id !== id)); // Remove the post from state
        navigate("/"); // Redirect after deleting
    };

    return (
        <DataContext.Provider value={{ search, setSearch, posts, setPosts, handleAddPost, handleDelete }}>
            {children}
        </DataContext.Provider>
    );
};

export default DataContext;
